var configurator = require('configurator');

//var local = require('./local.js');

/*
 // if you need secure parameters (logins etc)
 // put them in /config/local.js
 var local = require('./local.js');
 */

// configs for all environments (can also be used as a default value)
configurator.register({
	sails_environment: 'production',
	iamServiceRoot: 'https://iamws.intel.com/api/v1'
});

// configs for local
configurator.register({
	NODE_ENV: 'local',
	sails_port: process.env.PORT || 1337,
	sails_environment: 'development'
});

configurator.register({
	NODE_ENV: 'development',
	//iamServiceRoot: 'https://iam-dev.intel.com/api/v1',
	sails_port: process.env.PORT || 1337,
	sails_environment: 'development'
});

// configs for staging
configurator.register({
	NODE_ENV: 'staging',
	// use 'production' mode of sails when not under active development
	sails_environment: 'production'
});

// configs for production
configurator.register({
	NODE_ENV: 'production',
	sails_environment: 'production'
});

module.exports = configurator.config;
