/* eslint-env mocha */

var fs = require('fs')
var path = require('path')
var fileExists = require('file-exists')

var cacerts = require('../index')
// cacerts.debug = true
var expect = require('chai').expect

var nock = require('nock')('https://certificates.intel.com/repository/certificates')

var caCachePath = path.resolve(__dirname, '../caCache.js')
var caCacheWIPPath = path.resolve(__dirname, '../caCacheWIP.js')

describe('cacerts', function () {
  before('ensure cache files are gone', function () {
    deleteCaCache()
  })

  it('should match shipped certs initially', function (done) {
    deleteCaCache()
    loadGoodCerts()
    expect(cacerts.ca).to.deep.equal(shippedCerts)
    cacerts.waitIfUpdating()
      .then(function () {
        done()
      })
  })

  it('should create caCache if none there.', function (done) {
    deleteCaCache()
    loadGoodCerts()
    expect(cacerts.ca).to.deep.equal(shippedCerts)
    cacerts.waitIfUpdating()
      .then(function () {
        expect(fileExists(caCachePath)).to.equal(true)
        var caCache = require(caCachePath)
        delete require.cache[require.resolve(caCachePath)]
        expect(caCache).to.deep.equal(shippedCerts)
        done()
      })
  })

  it('should load different certs if requested', function (done) {
    deleteCaCache()
    nockFile('/PublicSHA2RootChain-Base64.zip')
    cacerts.config({
      certUrl: 'https://certificates.intel.com/repository/certificates/PublicSHA2RootChain-Base64.zip'
    })
      .then(function () {
        expect(cacerts.ca).to.deep.equal(publicCerts)
        cacerts.waitIfUpdating()
          .then(function () {
            expect(fileExists(caCachePath)).to.equal(true)
            var caCache = require(caCachePath)
            delete require.cache[require.resolve(caCachePath)]
            expect(caCache).to.deep.equal(publicCerts)
            loadGoodCerts()
            // return config to defaults.
            cacerts.config({
              certUrl: [
                'https://certificates.intel.com/repository/certificates/PublicSHA2RootChain-Base64.zip',
                'https://certificates.intel.com/repository/certificates/IntelSHA2RootChain-Base64.zip'
              ]
            }).then(function () { done() }, done)
          })
      })
      .catch(done)
  })

  it('should retry with shipped certs if using cache fails to load certs', function (done) {
    deleteCaCache()
    // { [Error: unable to get local issuer certificate] code: 'UNABLE_TO_GET_ISSUER_CERT_LOCALLY' }
    nock
      .get('/PublicSHA2RootChain-Base64.zip')
      .replyWithError({
        'message': 'unable to get local issuer certificate',
        'code': 'UNABLE_TO_GET_ISSUER_CERT_LOCALLY'
      })
    loadGoodCerts()
    expect(cacerts.ca).to.deep.equal(shippedCerts)
    cacerts.waitIfUpdating()
      .then(function () {
        expect(nock.isDone()).to.equal(true)
        done()
      })
      .catch(done)
  })

  it('should replace caCache with fresh cache data.', function (done) {
    deleteCaCache()
    loadGoodCerts()
    cacerts.ensureFreshCerts()
      .then(function () {
        var cas = cacerts.ca
        expect(cas).to.deep.equal(shippedCerts)
        nockFile('/PublicSHA2RootChain-Base64.zip')
        cacerts.config({
          certUrl: ['https://certificates.intel.com/repository/certificates/PublicSHA2RootChain-Base64.zip']
        })
          .then(function () {
            cas = cacerts.ca
            expect(cas).to.deep.equal(publicCerts)

            loadGoodCerts()
            cacerts.config({
              certUrl: [
                'https://certificates.intel.com/repository/certificates/PublicSHA2RootChain-Base64.zip',
                'https://certificates.intel.com/repository/certificates/IntelSHA2RootChain-Base64.zip'
              ]
            }).then(function () { done() }, done)
          })
      })
      .catch(done)
  })

  it('should not re cache if recently cached', function (done) {
    deleteCaCache()
    var modifiedTime
    loadGoodCerts()
    cacerts.config({
      timeToLive: 1 // 1 millisecond
    })
      .then(function () {
        modifiedTime = fs.statSync(caCachePath).mtime
        loadGoodCerts()
        return cacerts.ensureFreshCerts()
          .then(function () {
            expect(modifiedTime.getTime()).to.be.below(fs.statSync(caCachePath).mtime.getTime())
            modifiedTime = fs.statSync(caCachePath).mtime
            return cacerts.config({
              timeToLive: 1000 * 60 * 60 * 24 * 7 // 7 days
            })
              .then(function () {
                modifiedTime = fs.statSync(caCachePath).mtime
                return cacerts.ensureFreshCerts()
                  .then(function () {
                    expect(modifiedTime.getTime()).to.equal(fs.statSync(caCachePath).mtime.getTime())
                    done()
                  })
              })
          })
      })
      .catch(done)
  })

  it('should not replace caCache if bad zip.', function (done) {
    deleteCaCache()
    loadGoodCerts()
    expect(cacerts.ca).to.deep.equal(shippedCerts)
    cacerts.waitIfUpdating()
      .then(function () {
        nock
          .get('/PublicSHA2RootChain-Base64.zip')
          .replyWithFile(200, './test/index.test.js')
        return cacerts.config({
          timeToLive: 1
        })
          .then(function () {
            done(new Error('it was supposed to throw an error'))
          })
          .catch(function () {
            done()
          })
      })
      .catch(done)
  })

  it('should not replace caCache if no certs are found in zip.', function (done) {
    var modifiedTime
    deleteCaCache()
    nockFile('/PublicSHA2RootChain-Base64.zip')
    cacerts.config({
      certUrl: ['https://certificates.intel.com/repository/certificates/PublicSHA2RootChain-Base64.zip'],
      timeToLive: 1 // 1 millisecond
    })
      .then(function () {
        modifiedTime = fs.statSync(caCachePath).mtime
        nock
          .get('/PublicSHA2RootChain-Base64.zip')
          .replyWithFile(200, './test/empty.zip')
        return cacerts.ensureFreshCerts()
          .then(function () {
            done(new Error('it was supposed to throw an error'))
          })
          .catch(function () {
            expect(modifiedTime.getTime()).to.equal(fs.statSync(caCachePath).mtime.getTime())
            modifiedTime = fs.statSync(caCachePath).mtime
            loadGoodCerts()
            return cacerts.config({
              timeToLive: 1000 * 60 * 60 * 24 * 7, // 7 days
              certUrl: [
                'https://certificates.intel.com/repository/certificates/PublicSHA2RootChain-Base64.zip',
                'https://certificates.intel.com/repository/certificates/IntelSHA2RootChain-Base64.zip'
              ]
            }).then(function () { done() }, done)
          })
      })
      .catch(done)
  })

  after('check nock all done', function () {
    deleteCaCache()
    expect(nock.isDone(), 'nock was not emptied. ').to.equal(true)
  })
})

