// utility functions
function nockFile (filename) {
  nock
    .get(filename)
    .replyWithFile(200, './test' + filename)
}

function loadGoodCerts () {
  nockFile('/PublicSHA2RootChain-Base64.zip')
  nockFile('/IntelSHA2RootChain-Base64.zip')
}

function deleteCaCache () {
  if (fileExists(caCachePath)) {
    fs.unlinkSync(caCachePath)
  }
  if (fileExists(caCacheWIPPath)) {
    fs.unlinkSync(caCacheWIPPath)
  }
}
